import { CUSTOM_ELEMENTS_SCHEMA } from '@angular/core';
import { ModalController, ToastController } from '@ionic/angular';

import { async, ComponentFixture, TestBed, getTestBed } from '@angular/core/testing';

import { MensaPage } from './mensa.page';

import { TranslateModule, TranslateService} from '@ngx-translate/core';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { HttpClientTestingModule } from '@angular/common/http/testing';
import { DataService } from 'src/app/services/data.service';
import { StorageService } from 'src/app/services/storage.service';

let mensaData: any;
let spy: any;
describe('MensaPage', () => {
  let component: MensaPage;
  let fixture: ComponentFixture<MensaPage>;

  let  modalCtrlSpy: any;

  let injector: TestBed;
  let storageService: StorageService;

  beforeEach(async(() => {

   
    TestBed.configureTestingModule({
      declarations: [ MensaPage ],
      imports: [
        HttpClientTestingModule,
        TranslateModule.forRoot(),
      ],
      providers: [
        {provide: ModalController, useClass: MockCreateDummy },
        {provide: ToastController, useClass: MockCreateDummy },
        TranslateService,
        {
          provide: ActivatedRoute, useClass: MockActivatedRoute
        },
        { provide: DataService, useClass: MockDataService },
      ],
      schemas: [CUSTOM_ELEMENTS_SCHEMA],
    })
    .compileComponents();


    injector = getTestBed();
    modalCtrlSpy = injector.get(ModalController);
    storageService = injector.get(StorageService);
   
  }));

  beforeEach(() => {
    fixture = TestBed.createComponent(MensaPage);
    component = fixture.componentInstance;
    fixture.detectChanges();
  });

  it('should create', () => {
    mensaData = mensaData1;
    component.ionViewWillEnter();
    expect(component).toBeTruthy();
    component.ngOnInit();
    fixture.detectChanges();
  });

  

  it('current date not present, default select first entry', () => {
    mensaData = mensaData2;
    expect(component).toBeTruthy();
    component.ngOnInit().then(() => {
      fixture.detectChanges();
      expect(component.selectedDate).toEqual('2020-01-02T00:00:00.000Z');
    });
  });


  it('no meal data provided', () => {
    mensaData = JSON.parse('{"days":[]}');
    expect(component).toBeTruthy();
    component.ngOnInit().then(() => {
      fixture.detectChanges();
      expect(component.mealDataProvided).toEqual(false);
    });
  });


  it('feedback modal creation', async () => {
    mensaData = mensaData1;
    spy = jasmine.createSpyObj('ModalController', ['present']);
    const food = { 'name': 'Schnitzel', 'knownMealId': 'unkonwn', 'mensaId': 'sb'};
    await component.presentFeedbackModal(food, 'Komplett');

    expect(spy.present).toHaveBeenCalled();
  });

  it('present Toast', async () => {
    mensaData = mensaData1;
    spy = jasmine.createSpyObj('ToastController', ['present']);
    await component.presentToast('message', 100, 'test');

    expect(spy.present).toHaveBeenCalled();
  });

  it('reload data', async () => {
    mensaData = mensaData1;
    await component.ngOnInit();
    await component.reloadData();
    //console.log("---------------------");
    //console.log(component.meals);

    expect(component.loaded).toEqual(true);
  });

  
  it('try again button', async () => {
    mensaData = mensaData1;
    await component.ngOnInit();
    await component.tryAgain();
    //console.log("---------------------");
    //console.log(component.meals);

    expect(component.loaded).toEqual(true);
  });

  it('Network true: try again', async () => {
    const buffer = component.networkPlugin.getStatus;
    const reloadDataSpy = spyOn(component, 'reloadData');
    let testCalled = false;
    component.networkPlugin.getStatus = () => {
      testCalled = true;
      return {connected: true};
    };
    await component.tryAgain();
    expect(testCalled).toEqual(true);
    expect(reloadDataSpy).toHaveBeenCalled();
    component.networkPlugin.getStatus = buffer;
  });

  it('Network false: try again', async () => {
    const buffer = component.networkPlugin.getStatus;
    const reloadDataSpy = spyOn(component, 'reloadData');
    let testCalled = false;
    component.networkPlugin.getStatus = () => {
      testCalled = true;
      return {connected: false};
    };
    await component.tryAgain();
    expect(testCalled).toEqual(true);
    expect(reloadDataSpy).not.toHaveBeenCalled();
    component.networkPlugin.getStatus = buffer;
  });

  it('favourite meals', async () => {
    mensaData = mensaData1;
    await component.ngOnInit();

    const meal = 'Schnitzel';
    
    component.allFavoriteMeals = {'sb': [meal]};

    let ret = component.isFavorite(meal);
    expect(ret).toBeTruthy();

    const meals = ['pommes', 'affe'];
    meals.forEach(element => {
      ret = component.isFavorite(element);
      expect(ret).toBeFalsy();
    });

    component.allFavoriteMeals = {};
    ret = component.isFavorite(meal);
    expect(ret).toBeFalsy();

    component.allFavoriteMeals = {'sb': []};
    ret = component.isFavorite(meal);
    expect(ret).toBeFalsy();
  });

  it('adds meal to Favorite', async () => {
    
    mensaData = mensaData1;
    await component.ngOnInit();

    const meal = 'Schnitzel';
    const event = jasmine.createSpyObj('ev', ['stopPropagation']);
    
    await component.doMealFavorite(event, meal);
    expect(storageService.allFavoriteMeals[component.mensaId]).toContain(meal)
  

    spyOn(storageService, 'setFavoriteMeals')
    await component.doMealFavorite(event, meal);
    expect(storageService.setFavoriteMeals).toHaveBeenCalledTimes(1);
    expect(storageService.setFavoriteMeals).toHaveBeenCalledWith(component.mensaId, meal);
  });


  it('removes Meal from Favorite', async () => {
    mensaData = mensaData1;
    await component.ngOnInit();

    const meal = 'Schnitzel';
    const event = jasmine.createSpyObj('ev', ['stopPropagation']);
    
    //set favorite meal first to test if removed worked
    await component.doMealFavorite(event, meal);
    expect(storageService.allFavoriteMeals[component.mensaId]).toContain(meal)
  
    //remove
    await component.removeMealFavorite(event, meal);
    expect(storageService.allFavoriteMeals[component.mensaId]).not.toContain(meal)
  
    spyOn(storageService, 'removeSingleMeal')
    await component.removeMealFavorite(event, meal);
    expect(storageService.removeSingleMeal).toHaveBeenCalledTimes(1);
    expect(storageService.removeSingleMeal).toHaveBeenCalledWith(component.mensaId, meal);
  
  });

  it('checkFavoriteMealsServed', async () => {
    mensaData = mensaData1;
    await component.ngOnInit();

    spyOn(storageService, 'getFoodAlarmStatus');

    let favoriteMeals = ['pommes', 'schnitzel'];
    let mealsToday = [{'meals': [{'name': 'pommes'}, {'name': 'nudeln'}]}];
    component.mensaId = 'sb';
    component.allFavoriteMeals = {'sb': favoriteMeals};

    component.checkFavoriteMealsServed(mealsToday);

    expect(storageService.getFoodAlarmStatus).toHaveBeenCalled();
  });

  it('checks testSingleComponent', async () => {
    await component.ngOnInit();
    storageService.allergens = Object();
    storageService.allergens['mq'] = {"displayName":"Macadamia nuts", "isChecked": true};
    let result = component.testSingleComponent('mq');
    expect(result).toEqual(true);
  });

  it('checks testSingleComponent for component which is not contained in allergens ', async () => {
    let result = component.testSingleComponent('mxx');
    expect(result).toEqual(false);
  });

  it('events subscribe check: connected true', async () => {
    component.networkStatus = true;
    const reLoadDataSpy = spyOn(component, 'reloadData');
    await component.events.publish('language:changed');

    expect(reLoadDataSpy).toHaveBeenCalled();
  });

  it('events subscribe check: connected false', async () => {
    component.networkStatus = false;
    const reLoadDataSpy = spyOn(component, 'reloadData');
    await component.events.publish('language:changed');

    expect(reLoadDataSpy).not.toHaveBeenCalled();
  });

  it('Network true: addeventlistener connected true', async () => {
    let testCalled = false;
    const buffer = component.networkPlugin.addListener;
    const reLoadDataSpy = spyOn(component, 'reloadData');
    component.networkPlugin.addListener = (a, b) => {
      testCalled = true;
      b({connected: true});
    };
    await component.ngOnInit();
    expect(testCalled).toEqual(true);
    expect(reLoadDataSpy).toHaveBeenCalled();
    component.networkPlugin.addListener = buffer;
  });

  it('Network false: addeventlistener connected false', async () => {
    let testCalled = false;
    const buffer = component.networkPlugin.addListener;
    const reLoadDataSpy = spyOn(component, 'reloadData');
    component.networkPlugin.addListener = (a, b) => {
      testCalled = true;
      b({connected: false});
    };
    await component.ngOnInit();
    expect(testCalled).toEqual(true);
    expect(reLoadDataSpy).not.toHaveBeenCalled();
    component.networkPlugin.addListener = buffer;
  });

});


class MockCreateDummy {
  create(data) {
    return spy;
  }
}


class MockDataService {
  retData = {'sb': 'blablabla', 'notices': ['abc', 'def']};
  getMensaData(mensaId) {
    return  new Observable(observer => {observer.next(mensaData); });
  }
  getBaseData() {
    return  new Observable(observer => {observer.next(this.retData); });
  }
}

class MockParamMap {
  get(identifier: string) {
    if (identifier === 'mensaId') {
      return 'sb';
    }
  }
}

class MockSnapshot {
  paramMap = new MockParamMap();
  constructor() {}
}

class MockActivatedRoute {
  snapshot = new MockSnapshot();
  constructor() {}
}


